/** @format */

export const shopItems = (stateItems, item) => {
	const isItemExist = stateItems.find((im) => im.id === item.id);

	if (isItemExist) {
		return stateItems.map((im) =>
			im.id === item.id ? { ...im, quantity: im.quantity + 1 } : { ...im },
		);
	} else {
		return [...stateItems, { ...item, quantity: 1 }];
	}
};
