/** @format */

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { auth, createDataBaseForUser } from '../../firabase/fireBaseUtils';
import Button from '../../components/button/button.component';

import Input from '../../components/input/input';

import './register.scss';

const Register = () => {
	const [registerInfo, setRegisterInfo] = useState({
		name: '',
		email: '',
		password: '',
		confirmPassword: '',
	});

	const { email, password, name, confirmPassword } = registerInfo;

	const handleChange = (event) => {
		const { name, value } = event.target;
		setRegisterInfo({
			[name]: value,
		});
	};

	const handleSubmit = async () => {
		if (
			password === confirmPassword &&
			password.length &&
			confirmPassword.length
		) {
			const { user } = await auth.createUserWithEmailAndPassword(
				email,
				password,
			);
			console.log('register');
			await createDataBaseForUser(user, { name });
		}
		this.setRegisterInfo({
			name: '',
			email: '',
			password: '',
			confirmPassword: '',
		});
	};
	return (
		<div className='register_container '>
			<div className='register_form  '>
				<p>Please provide registering details here :</p>
				<Input
					label='Name:'
					name='name'
					value={name}
					placeholder='name'
					type='text'
					inputChange={handleChange}
				/>
				<Input
					label='Email:'
					name='email'
					value={email}
					placeholder='email'
					type='email'
					inputChange={handleChange}
				/>
				<Input
					label='Password:'
					name='password'
					value={password}
					placeholder='password'
					type='password'
					inputChange={handleChange}
				/>
				<Input
					label='Confirm Password:'
					name='confirmPassword'
					value={confirmPassword}
					placeholder='email'
					type='password'
					inputChange={handleChange}
				/>
				<div className='btn'>
					<Button btAction={handleSubmit}>Register</Button>
				</div>
				<h4>
					If registered click
					<Link to='/signIn'>
						<span> "signIn"</span>
					</Link>
				</h4>
			</div>
		</div>
	);
};

export default Register;
