/** @format */

import React from 'react';
import { connect } from 'react-redux';
import {
	addItemToCart,
	reduceItemFromCart,
	deleteItemFromCart,
	deleteAllItems,
} from '../../redux/cart/cart.actions';
import { reduceItems } from '../../redux/cart/cartUtils';

import './checkOutItems.scss';

const CheckOutItems = ({
	item,
	reduceItem,
	addItem,
	deleteItem,
	deleteAllItems,
}) => {
	return (
		<div className='check_out_items'>
			<div className='check_Out_header'>
				<img alt='' className='check_Out_image' src={item.imageUrl} />
				<span>{item.name}</span>
			</div>
			<div className='header'>
				<span>{item.price}</span>
			</div>
			<div className='header'>
				<span>
					<span className='br' onClick={(item) => reduceItem(item)}>
						<i class='fas fa-less-than'></i>
					</span>
					{item.quantity}{' '}
					<span onClick={(item) => addItem(item)}>
						<i class='fas fa-greater-than'></i>
					</span>
				</span>
			</div>
			<div className='header'>
				<span>{item.price * item.quantity}</span>
			</div>
			<div className='header'>
				<span onClick={(item) => deleteItem(item)}>
					<i class='far fa-trash-alt'></i>
				</span>
			</div>
		</div>
	);
};

const mapDispatchToProps = (dispatch) => ({
	reduceItem: (item) => dispatch(reduceItemFromCart(item)),
	addItem: (item) => dispatch(addItemToCart(item)),
	deleteItem: (item) => dispatch(deleteItemFromCart(item)),
	deleteAllItems: () => dispatch(deleteAllItems()),
});

export default connect(null, mapDispatchToProps)(CheckOutItems);
