/** @format */

import React from 'react';
import { Redirect, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

import { getCurrentUser, getUserSinghOut } from '../src/redux/user/user.action';
import {
	auth,
	createDataBaseForUser,
	createDataBase,
} from '././firabase/fireBaseUtils';
import Header from './components/header/header.component';
import HomePage from './pages/directory/HomePage.page';
import ShopPage from './pages/shopPage/shopPage';
import SignIn from './pages/signIn/signIn';
import './App.css';
import Register from './pages/register/register';
import CheckOut from './pages/checkOut/checkOut';

// import SHOP_DATA from '../src/redux/collections/shop.data.js';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentUser: '',
		};
	}

	handleSignOut = () => {
		// auth.signOut().then(() => this.setState({ currentUser: '' }));
		auth.signOut().then(() => this.props.getUserSinghOut());
	};

	subscribeGoogleAuth = null;

	componentDidMount() {
		this.subscribeGoogleAuth = auth.onAuthStateChanged(async (user) => {
			console.log(user);
			if (user) {
				console.log('mount');
				const userRef = await createDataBaseForUser(user);

				// userRef.get().then((doc) => {
				// 	console.log('this');
				// 	this.setState({ currentUser: doc.data() });
				// });
				userRef.get().then((doc) => this.props.getCurrentUser(doc.data()));
			}
		});
		// this function for one time use , to create fresh data base in fire store
		// createDataBase(SHOP_DATA);
	}
	componentWillUnmount() {
		this.subscribeGoogleAuth = null;
	}
	// componentDidUpdate() {
	// 	this.subscribeGoogleAuth = auth.onAuthStateChanged(async (user) => {
	// 		if (user && user.displayName !== null) {
	// 			console.log('mount');
	// 			const userRef = await createDataBaseForUser(user);

	// 			// userRef.get().then((doc) => {
	// 			// 	console.log('this');
	// 			// 	this.setState({ currentUser: doc.data() });
	// 			// });
	// 			userRef.get().then((doc) => this.props.getCurrentUser(doc.data()));
	// 		}
	// 	});
	// }
	render() {
		console.log(this.props.currentUser);
		return (
			<>
				<Header
					currentUser={this.props.currentUser}
					handleSignOut={this.handleSignOut}
				/>
				<Switch>
					<Route exact path='/' component={HomePage} />
					<Route path='/shop' component={ShopPage} />
					<Route
						path='/signIn'
						render={() =>
							this.props.currentUser ? <Redirect to='/' /> : <SignIn />
						}
					/>
					<Route
						path='/register'
						render={() =>
							this.props.currentUser ? <Redirect to='/' /> : <Register />
						}
					/>
					<Route path='/checkOut' component={CheckOut} />
				</Switch>
			</>
		);
	}
}

const mapStateToProps = (state) => ({
	currentUser: state.user.currentUser,
});

const mapDispatchToProps = (dispatch) => ({
	getCurrentUser: (currentUser) => dispatch(getCurrentUser(currentUser)),
	getUserSinghOut: () => dispatch(getUserSinghOut()),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
