/** @format */

import userActionsTypes from './user.action.types';
import { AppActions } from '../../types/user/action';
import { Payload } from '../../types/user/user';

export const getCurrentUser = (currentUser: Payload): AppActions => ({
	type: userActionsTypes.GET_CURRENT_USER,
	payload: currentUser,
});

export const getUserSinghOut = () => ({
	type: userActionsTypes.SIGN_OUT,
});

export const googleSignInStart = () => ({
	type: userActionsTypes.GOOGLE_SIGN_IN_START,
});

export const googleSignInSuccess = (currentUser: Payload): AppActions => ({
	type: userActionsTypes.GOOGLE_SIGN_IN_SUCCESS,
	payload: currentUser,
});

export const googleSignInFails = (error: Payload): AppActions => ({
	type: userActionsTypes.GOOGLE_SIGN_IN_FAIL,
	payload: error,
});

export const emailSignInStart = (emailAndPassword: any) => ({
	type: userActionsTypes.EMAIL_SIGN_IN_START,
	payload: emailAndPassword,
});

export const emailSignInSuccess = (currentUser: Payload): AppActions => ({
	type: userActionsTypes.EMAIL_SIGN_IN_SUCCESS,
	payload: currentUser,
});

export const emailSignInFails = (error: string) => ({
	type: userActionsTypes.EMAIL_SIGN_IN_FAIL,
	error,
});

export const checkUser = (currentUser: null) => ({
	type: userActionsTypes.CHECK_USER,
	currentUser,
});

export const isUserAuthenticateSuccess = (
	currentUser: Payload,
): AppActions => ({
	type: userActionsTypes.USER_LOGIN_SUCCESS,
	payload: currentUser,
});
