/** @format */

import React from 'react';
import { Link } from 'react-router-dom';

import Button from '../../components/button/button.component';

import Input from '../../components/input/input';
import { googleSignIn, auth } from '../../firabase/fireBaseUtils.js';
import './signIn.scss';

class SignUp extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: '',
		};
	}

	handleChange = (event) => {
		const { name, value } = event.target;
		this.setState(
			{
				[name]: value,
			},
			() => {
				console.log(this.state);
			},
		);
	};

	handleSignIn = async (event) => {
		event.preventDefault();
		const { email, password } = this.state;

		if (email.length && password.length) {
			await auth.signInWithEmailAndPassword(email, password);
		}
	};

	render() {
		return (
			<div className='signUp_container'>
				<div className='signUp_form'>
					<p>Please provide signIn details here :</p>
					<Input
						label='Email:'
						name='email'
						value={this.state.email}
						placeholder='email'
						type='text'
						inputChange={this.handleChange}
					/>
					<Input
						label='Password:'
						name='password'
						value={this.state.password}
						placeholder='password'
						type='password'
						inputChange={this.handleChange}
					/>
					<div className='btn'>
						<Button btAction={this.handleSignIn}>Sign In</Button>
					</div>
					<div className='btn'>
						<Button btAction={googleSignIn} isGoogle='isGoogle'>
							Sign In With Google
						</Button>
					</div>
					<h4>
						If not registered click
						<Link to='/register'>
							<span> "Register"</span>
						</Link>
					</h4>
				</div>
			</div>
		);
	}
}

export default SignUp;
