/** @format */

import { shopActionTypes } from './cart.actionTypes';

const INITIAL_STATE = {
	shopItems: [],
};

const shopReducer = (state = INITIAL_STATE, action = {}) => {
	switch (action.type) {
		case shopActionTypes.ADD_ITEM_TO_CART:
			return {
				...state,
				shopItems: [...state, action.payload],
			};

		default:
			return state;
	}
};

export default shopReducer;
