/** @format */

import { put, takeLatest, call, all } from 'redux-saga/effects';
import {
	googleSignIn,
	createDataBaseForUser,
} from '../../firabase/fireBaseUtils';
import userActionsTypes from './user.action.types';
import { googleSignInFails, googleSignInSuccess } from './user.action';
import { fetchCollectionsAsync } from '../collections/collections.actions';

function* signInWithGoogle() {
	try {
		const { user } = yield call(googleSignIn);
		console.log(user);
		const fetchUser = async (user) => await call(createDataBaseForUser(user));
		const userRef = yield call(fetchUser, user);
		console.log(userRef);
	} catch (error) {}
}

export function* onGoogleSignInStart() {
	yield takeLatest(userActionsTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* userSagas() {
	yield all([call(onGoogleSignInStart)]);
}
