/** @format */

// Firebase App (the core Firebase SDK) is always required and
// must be listed before other Firebase SDKs
import firebase from 'firebase/app';

// Add the Firebase services that you want to use
import 'firebase/auth';
import 'firebase/firestore';

// TODO: Replace the following with your app's Firebase project configuration
// For Firebase JavaScript SDK v7.20.0 and later, `measurementId` is an optional field
const firebaseConfig = {
	apiKey: 'AIzaSyDHWOXbyk0nwKOojf64jIZohJgqePtJgSM',
	authDomain: 'crown-65cf0.firebaseapp.com',
	databaseURL: 'https://crown-65cf0.firebaseio.com',
	projectId: 'crown-65cf0',
	storageBucket: 'crown-65cf0.appspot.com',
	messagingSenderId: '925472468806',
	appId: '1:925472468806:web:1c2132c8be289027fdff47',
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);

export const fireStore = firebase.firestore();

export const createDataBaseForUser = async (user, otherData) => {
	const userRef = fireStore.collection('users').doc(`${user.uid}`);
	const userSnapShot = await userRef.get();

	if (userSnapShot.exists) {
		return userRef;
	} else {
		const { displayName, email } = user;

		userRef.set({
			displayName,
			email,
			createdAt: new Date(),
			...otherData,
		});
		return userRef;
	}
};

export const createDataBase = (collectionArray) => {
	collectionArray.forEach((item) => {
		fireStore
			.collection('collections')
			.doc(`${item.title}`)
			.set({
				...item,
			});
	});
};

export const fetchCollectionsFromFireStore = async () => {
	const querySnapshot = await fireStore.collection('collections').get();
	const collections = [];
	querySnapshot.forEach(function (doc) {
		// doc.data() is never undefined for query doc snapshots

		const collection = doc.data();
		collections.push(collection);
	});

	return collections;
};

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({ prompt: 'select_account' });

export const googleSignIn = () => firebase.auth().signInWithPopup(provider);
export const auth = firebase.auth();
