/** @format */

import { collectionActions } from './user.action.types';

const INITIAL_STATE = {
	currentUser: null,
};

const userReducer = (state = INITIAL_STATE, action = {}) => {
	switch (action.type) {
		case collectionActions.UP_DATE_COLLECTIONS:
			return {
				...state,
				currentUser: action.payload,
			};

		default:
			return state;
	}
};

export default userReducer;
