/** @format */

import { createSelector } from 'reselect';

const getCart = (state) => {
	return state.cart;
};

export const isHidden = createSelector([getCart], (cart) => cart.isHidden);

export const getShopItems = createSelector(
	[getCart],

	(cart) => cart.shopItems,
);
